import React from 'react';
import PropTypes from 'prop-types';
import {
  IconButton,
  ListItem,
  ListItemText,
  ListItemSecondaryAction
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

import {
  Info,
  CheckBoxOutlineBlank as CheckBoxBlank,
  Delete,
  Mail,
  ExpandLess,
  ExpandMore,
  StarBorder
  
} from '@material-ui/icons';
import { green } from '@material-ui/core/colors';
import { ICON_SIZE } from '../../App';
import List from '@material-ui/core/List';
import Collapse from '@material-ui/core/Collapse';

import ListItemIcon from '@material-ui/core/ListItemIcon';


import InboxIcon from '@material-ui/icons/MoveToInbox';



//import DetailsTask from './DetailsTask';
//import ListItemButton from '@mui/material/ListItemButton';
//import ListItemIcon from '@mui/material/ListItemIcon';

//import InboxIcon from '@mui/icons-material/MoveToInbox';
//import ExpandLess from '@mui/icons-material/ExpandLess';
//import ExpandMore from '@mui/icons-material/ExpandMore';
//import StarBorder from '@mui/icons-material/StarBorder';

/**
 * A single task item
 *
 * @param {Number} id task unique id
 * @param {String} text description of the task
 * @param {Boolean} completed Task is either completed or not
 * @param {Function} onClick Handles clicking on a task and on the checkbox icon
 * @param {Function} onDeleteClick Handles clicking on the trash icon
 */

 const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
}));
const TaskItem = ({
  name,
  description,
  duedate,
  onClick,
  onDeleteClick
}) => {
  
  const classes = useStyles();

  const [open, setOpen] = React.useState(true);

const handleClickOpen = () => {
  setOpen(!open);
};

  const handleClick = () => {
    onClick()
    
    console.log("handleClick");
  };
  const handleDeleteClick = () => onDeleteClick();

  const renderCheckboxIcon = () => {
     return <Info style={{ color: green[500] }} fontSize={ICON_SIZE} />
     //<Info style={{ color: green[500] }} fontSize={ICON_SIZE} />
     //<button><Link to={`/d`}>Buy Now</Link></button>;
    // if (completed) {
    //   // Task is completed - render green checked icon
    //   return <CheckBox style={{ color: green[500] }} fontSize={ICON_SIZE} />
    // }
    // // By default render unchecked icon
    // return <CheckBoxBlank fontSize={ICON_SIZE} />
 }
//console.log("TaskItem",text,completed,onClick,onDeleteClick);
  


console.log("TaskItem",name,description,duedate);
  return (
    <ListItem button onClick={handleClick}>
      
      <ListItem button onClick={handleClick}>
        <ListItemIcon>
          <InboxIcon />
        </ListItemIcon>
        <ListItemText primary="Inbox" />
        {open ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={open} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
              <StarBorder />
            </ListItemIcon>
            <ListItemText primary="Starred" />
          </ListItem>
		  </List>
      </Collapse>

      <ListItemText style={{textAlign: "center"}}  primary={name} />
      <ListItemText style={{textAlign: "center"}}  primary={description} />
      <ListItemText style={{textAlign: "center"}} primary={duedate} />
      <ListItemSecondaryAction >
        <IconButton
        
          edge="end"
          aria-label="delete the task"
          onClick={handleDeleteClick}
        >
          <Delete color="secondary" fontSize={ICON_SIZE} />
        </IconButton>
      </ListItemSecondaryAction>
    </ListItem>
    
  );
};
TaskItem.propTypes = {
  onClick: PropTypes.func.isRequired,
  onDeleteClick: PropTypes.func.isRequired,
  //completed: PropTypes.bool.isRequired,
  name: PropTypes.string.isRequired,
  description:PropTypes.string.isRequired,
  duedate:PropTypes.string.isRequired,
};

export default TaskItem;
