import React, { useState, useRef } from 'react';
import { connect } from 'react-redux';
import { ListItem, TextField, IconButton } from '@material-ui/core';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import { AddTaskAction } from '../actions/AddTaskAction';
import { ICON_SIZE } from '../../App';


/**
 * Creates new task
 * 
 * @param {Function} addTak Dispatches an action that creates a new task
 */
const AddTask = ({ AddTaskAction }) => {
  const [inputTextName, setInputTextName] = useState('');
  const [inputTextDescription, setInputTextDescription] = useState('');
  const values = {
    someDate: new Date().toISOString().substring(0, 10)
  };
  const [inputTextDate, setInputTextDate] = React.useState(values.someDate);
  
  const inputElement = useRef();

  const onSubmit = (e) => {
    console.log("On Submit",e,inputTextName,inputTextDescription,inputTextDate);
    e.preventDefault();

    // Dispatch action that adds new task
    AddTaskAction(inputTextName,inputTextDescription,inputTextDate);

    // Reset input field and focus it
    setInputTextName('');
    setInputTextDescription('');
    setInputTextDate(values.someDate);
    
    inputElement.current.focus();
  }

  return (

    <ListItem 
      component="form"
      onSubmit={onSubmit}
    >
      <IconButton 
        edge="start" 
        aria-label="create new task"
        type="submit"
      >
        <AddCircleIcon color="primary" fontSize={ICON_SIZE} />
      </IconButton>
      <TextField
        ref={inputElement}
        value={inputTextName}
        onChange={e => setInputTextName(e.target.value)}
        id="outlined-basic"
        label="Task Name"
        variant="outlined"
        required
        fullWidth
      />
      <TextField
        ref={inputElement}
        value={inputTextDescription}
        onChange={e => setInputTextDescription(e.target.value)}
        id="outlined-basic"
        label="Task Description"
        variant="outlined"
        required
        fullWidth
      />
      {/* <TextField
  id="datetime-local"
  label="End Time"
  type="datetime-local"
  defaultValue={moment().format('YYYY-MM-DDTHH:mm')}
  clearable={true}
  InputLabelProps={{
    shrink: true,
  }}
/>      */}
      
      <TextField
      ref={inputElement}
      value={inputTextDate}
      onChange={e => setInputTextDate(e.target.value)}
      id="outlined-basic"
      label="Deadline"
      variant="outlined"
      required
      fullWidth
        // InputLabelProps={{ shrink: true, required: true }}
        type="date"
        //defaultValue={values.someDate}
      />
    </ListItem>

  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    
    AddTaskAction: (name,description,duedate) => dispatch(AddTaskAction(name,description,duedate))
  }
}

export default connect(null, mapDispatchToProps)(AddTask);
