import React, { useState, useRef } from 'react';
import { connect } from 'react-redux';
import { ListItem, TextField, IconButton } from '@material-ui/core';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import { AddTaskAction } from '../actions/AddTaskAction';
import { ICON_SIZE } from '../../App';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';


/**
 * Creates new task
 * 
 * @param {Function} addTak Dispatches an action that creates a new task
 */
const AddTask = ({ AddTaskAction }) => {
  const [inputTextName, setInputTextName] = useState('');
  const [inputTextDescription, setInputTextDescription] = useState('');
  const [inputTextDate, setInputTextDate] = React.useState(null);

  const inputElement = useRef();

  const onSubmit = (e) => {
    console.log("On Submit",e,inputTextName,inputTextDescription);
    e.preventDefault();

    // Dispatch action that adds new task
    AddTaskAction(inputTextName,inputTextDescription);

    // Reset input field and focus it
    setInputTextName('');
    setInputTextDescription('');
    inputElement.current.focus();
  }

  return (
    <ListItem 
      component="form"
      onSubmit={onSubmit}
    >
      <IconButton 
        edge="start" 
        aria-label="create new task"
        type="submit"
      >
        <AddCircleIcon color="primary" fontSize={ICON_SIZE} />
      </IconButton>
      <TextField
        ref={inputElement}
        value={inputTextName}
        onChange={e => setInputTextName(e.target.value)}
        id="outlined-basic"
        label="Task Name"
        variant="outlined"
        required
        fullWidth
      />
      <TextField
        ref={inputElement}
        value={inputTextDescription}
        onChange={e => setInputTextDescription(e.target.value)}
        id="outlined-basic"
        label="Task Description"
        variant="outlined"
        required
        fullWidth
      />
      <DatePicker
        label="Basic example"
        value={inputTextDate}
        onChange={(newValue) => {
          setInputTextDate(newValue);
        }}
        renderInput={(params) => <TextField {...params} />}
      />
    </ListItem>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    
    AddTaskAction: (name,description) => dispatch(AddTaskAction(name,description))
  }
}

export default connect(null, mapDispatchToProps)(AddTask);
