import { connect } from 'react-redux';
import { ActionTypes } from '../constants/ActionTypes';
import { ToggleTaskAction } from '../actions/ToggleTaskAction';
import { RemoveTaskAction } from '../actions/RemoveTaskAction';
import TaskList from '../components/TaskList';

const getVisibleTaskList = (tasks, filter) => {
  // switch (filter) {
  //   case ActionTypes.SHOW_COMPLETED:
  //     return tasks.filter((task) => task.completed);
  //   case ActionTypes.SHOW_ACTIVE:
  //     return tasks.filter((task) => !task.completed);
  //   case ActionTypes.SHOW_ALL:
  //   default:
  {
      return tasks;
      console.log(tasks);
  }
};

const mapStateToProps = (state) => {
  return {
    tasks: getVisibleTaskList(state.tasks, state.visibilityFilter),
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onTaskClick: (id) => dispatch(ToggleTaskAction(id)),
    onTaskDeleteClick: (id) => dispatch(RemoveTaskAction(id))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(TaskList);
